mapper.update:
UPDATE xxlmq_queue_consumer
SET update_time = NOW()
WHERE queue_name= #{queueName}
	AND consumer_uuid = #{consumerUuid}

mapper.select:
SELECT * FROM (
	SELECT *,
		(@rownum := @rownum + 1) rank, 
		(SELECT COUNT(id) FROM xxlmq_queue_consumer) as count
	FROM xxlmq_queue_consumer, 
		(SELECT @rownum := -1) r
	ORDER BY id DESC
) AS t2 
WHERE queue_name = #{queueName} 
	AND consumer_uuid = #{consumerUuid}
